using System.Collections.Generic;using MvvmCross.Commands;using MvvmCross.Navigation;using MvvmCross.ViewModels;namespace Iubh.RadioApp.Core.ViewModels{    public class OnboardingViewModel : MvxViewModel<bool>    {        public bool FromService { get; set; }        private List<PageViewModel> pages;        public List<PageViewModel> Pages        {            get => this.pages;            set            {                this.pages = value;                this.RaisePropertyChanged(() => this.Pages);            }        }        private int selectedPage;        public int SelectedPage        {            get => this.selectedPage;            set            {                this.selectedPage = value;                this.IsLastPage = this.Pages.Count - 1 == value;                this.RaisePropertyChanged(() => this.SelectedPage);            }        }        private bool isLastPage;        public bool IsLastPage        {            get => this.isLastPage;            set            {                this.isLastPage = value;                this.RaisePropertyChanged(() => this.IsLastPage);            }        }        public IMvxCommand NextCommand { get; }        public IMvxCommand TurnOverCommand { get; }        private readonly IMvxNavigationService navigationService;        public OnboardingViewModel(IMvxNavigationService navigationService)        {            this.navigationService = navigationService;            this.Pages = new List<PageViewModel>()             {                new PageViewModel { Page = Options.OnboardingOption.Welcome, Headline = "Willkommen in der Radio App!", Image = "onboarding_welcome", Text = "Ihren Lieblingsradiosender finden Sie nun auf Ihrem Smartphone."},                new PageViewModel { Page = Options.OnboardingOption.Listen, Headline = "Radio hören", Image = "onboarding_listen", Text = "Hören Sie Ihren Lieblingsradiosender mobil auf Ihrem Smartphone."},                new PageViewModel { Page = Options.OnboardingOption.Wish, Headline = "Musik wünschen", Image = "onboarding_wish", Text = "Sie können Ihren Musikwunsch über die App dem Moderator mitteilen."},                new PageViewModel { Page = Options.OnboardingOption.Rate, Headline = "Bewertungen", Image = "onboarding_rate", Text = "Bewerten Sie die Playlist und den Moderator, helfen Sie uns dabei, besser zu werden."},                new PageViewModel { Page = Options.OnboardingOption.Service, Headline = "Service", Image = "onboarding_service", Text = "Teilen und Bewerten Sie die App, kontaktieren Sie bei Fragen oder Störungen unseren Support."},                new PageViewModel { Page = Options.OnboardingOption.Moderator, Headline = "Moderator-Login",  Image = "onboarding_moderator", Text = "Als Moderator können Sie die Bewertungen der Playlist und des Moderators sehen."}            };            this.NextCommand = new MvxCommand(this.Next);            this.TurnOverCommand = new MvxCommand(this.TurnOver);        }        private void Next()        {            if (this.SelectedPage < this.Pages.Count - 1)            {                this.SelectedPage = this.SelectedPage + 1;            }            else            {                this.navigationService.Navigate<TabBarViewModel>();            }        }        private void TurnOver()        {            if (this.FromService == true)            {                this.navigationService.Close(this);            }            else                this.navigationService.Navigate<TabBarViewModel>();        }        public override void Prepare(bool parameter)        {            this.FromService = true;        }    }}