using System;namespace Iubh.RadioApp.Data.Database.Commands{    public class InsertVersionCommand : ISqlCommand    {        private readonly Models.Version version;        public InsertVersionCommand(Models.Version version)        {            this.version = version;        }        public string GetCommand()        {            return "INSERT INTO [Version] (Nr) VALUES(?)";        }        public object[] GetParams()        {            var paramz = new object[]            {                this.version.Nr.ToString()            };            return paramz;        }    }}