using Iubh.RadioApp.Core.Options;using MvvmCross.ViewModels;namespace Iubh.RadioApp.Core.ViewModels{    public class PageViewModel : MvxViewModel    {        private OnboardingOption page;        public OnboardingOption Page        {            get => this.page;            set            {                this.page = value;                this.RaisePropertyChanged(() => this.Page);            }        }        public string Image { get; set; }        public string Text { get; set; }        public string Headline { get; set; }        public static PageViewModel From(OnboardingOption page, string text, string image, string headline)        {            return new PageViewModel            {                Page = page,                Text = text,                Image = image,                Headline = headline            };        }    }}